import "./App.css";
import { useState, useEffect } from "react";
import * as BookAPI from './BooksAPI.js'
import Shelf from './components/Shelf'

function App() {
  const [showSearchPage, setShowSearchPage] = useState(false);
  const [shelves, setShelves] = useState({});

  useEffect(()=>{
    BookAPI.getAll().then((allBooks)=>{
      const shelves = Array.from(new Set(allBooks.map((book)=>book.shelf)));
      setShelves(shelves)
      let dictionary = {};
      shelves.forEach(shelf => {
        dictionary[shelf] = allBooks.filter((book)=> book.shelf === shelf)
      });
      setShelves(dictionary);
    }).catch((error)=> console.log(error));
  }, [])

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchPage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              {
                Object.keys(shelves).forEach((key, index)=>{
                  <Shelf key={i} shelfTitle={key} books={shelves[key]} onShelfChange={()=>{}}/>
                })
              }
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchPage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
