import { useState } from "react";
import * as BooksAPI from "../BooksAPI.js";
import { Link } from "react-router-dom";
import Book from "./Book";

const SearchPage = ({ updateScreen }) => {
  const [searchRes, setSearchRes] = useState([]);
  const [searchWord, setSearchWord] = useState([]);
  const [isVisible, setIsVisible] = useState(true);

  function searchHandler(event) {
    setSearchRes([]);
    setSearchWord(event.target.value.trim());
    if (searchWord !== "") {
      BooksAPI.search(searchWord)
        .then((res) => {
            let values = [];
          Array.isArray(res) ? values = res : setSearchRes([]);
          let now = values.filter((value) => "imageLinks" in value !== false && "authors" in value !== false);
          setSearchRes(now.length > 0 ? now : [])
        })
        .catch((error) => console.log(error));
    }
    setIsVisible(!isVisible);
  }

  const [myBooks, setMyBooks] = useState([]);
  function updateSearch() {
    BooksAPI.getAll()
      .then((allBooks) => {
        setMyBooks(allBooks);
      })
      .catch((error) => console.log(error));
  }

  return (
    <div className="app">
      <div className="search-books">
        <div className="search-books-bar">
          <Link to="/" className="close-search">
            Close
          </Link>
          <div className="search-books-input-wrapper">
            <input
            value={searchWord}
              onChange={searchHandler}
              type="text"
              placeholder="Search by title, author, or ISBN"
            />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            {searchRes.map((book) => (
              <Book key={book.id} book={book} updateScreen={updateSearch} />
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
};

export default SearchPage;
