import "./App.css";
import {useState} from 'react';
import { Router,Route } from "react-router-dom";
import SearchPage from './components/SearchPage';
import MainPage from './components/mainPage';
import * as BooksAPI from './BooksAPI.js';

function App() {
  const [shelves, setShelves] = useState([]);
  function updateScreen() {
    BooksAPI.getAll()
      .then((allBooks) => {
        const sh = Array.from(new Set(allBooks.map((book) => book.shelf)));
        const arr = [];
        sh.forEach((shelf) => {
          var obj = {};
          obj[shelf] = allBooks.filter((book) => book.shelf === shelf);
          arr.push(obj);
        });
        setShelves(arr);
      })
      .catch((error) => console.log(error));
  }

  return (
    <Router>
      <Route exact path="/">
        <MainPage updateScreen={updateScreen} shelf/>
      </Route>
      <Route exact path="/search">
        <SearchPage updateScreen={updateScreen}/>
      </Route>
    </Router>
  );
}

export default App;
