import "./App.css";
import { useState } from "react";
import { Route, Routes } from "react-router-dom";
import SearchPage from "./components/SearchPage";
import MainPage from "./components/mainPage";
import * as BooksAPI from "./BooksAPI.js";

function App() {
  const [shelves, setShelves] = useState([]);
  function updateScreen() {
    BooksAPI.getAll()
      .then((allBooks) => {
        const sh = Array.from(new Set(allBooks.map((book) => book.shelf)));
        const arr = [];
        sh.forEach((shelf) => {
          var obj = {};
          obj[shelf] = allBooks.filter((book) => book.shelf === shelf);
          arr.push(obj);
        });
        setShelves(arr);
      })
      .catch((error) => console.log(error));
  }

  return (
    <div>
      <Routes>
      <Route exact path="/" element={<MainPage updateScreen={updateScreen} shelv />} />
      <Route exact path="/search" element={<MainPage />} />
      </Routes>
    </div>
  );
}

export default App;
