import React from "react";
import Book from './Book.js'
const Shelf = (books, shelfTitle, onChange) => {
  return (
    <div className="bookshelf">
      <h2 className="bookshelf-title">{shelfTitle}</h2>
      <div className="bookshelf-books">
        <ol className="books-grid">
            {books.map((book)=>{
                return<Book key= {book.id} imageUrl={book.imageUrl} shelf={book.shelf} title={book.title} author={book.author} onChange={onShelfChange} />
            })}
        </ol>
      </div>
    </div>
  );
};

export default Shelf;
