import "./App.css";
import { useState, useEffect } from "react";
import * as BookAPI from "./BooksAPI.js";
import Shelf from "./components/Shelf";
import Book from "./components/Book";

function App() {
  const [showSearchPage, setShowSearchPage] = useState(false);
  const [shelves, setShelves] = useState([]);

  const [searchRes, setSearchRes] = useState([]);
  const [searchWord, setSearchWord] = useState("")

  function updateScreen() {
    BookAPI.getAll()
      .then((allBooks) => {
        const sh = Array.from(new Set(allBooks.map((book) => book.shelf)));
        const arr = [];
        sh.forEach((shelf) => {
          var obj = {};
          obj[shelf] = allBooks.filter((book) => book.shelf === shelf);
          arr.push(obj);
        });
        setShelves(arr);
      })
      .catch((error) => console.log(error));
  }

  useEffect(() => {
    BookAPI.getAll()
      .then((allBooks) => {
        const sh = Array.from(new Set(allBooks.map((book) => book.shelf)));
        const arr = [];
        sh.forEach((shelf) => {
          var obj = {};
          obj[shelf] = allBooks.filter((book) => book.shelf === shelf);
          arr.push(obj);
        });
        setShelves(arr);
      })
      .catch((error) => console.log(error));
  }, []);

  function searchHandler(event) {
    if(event.target.value !== ""){
    BookAPI.search(event.target.value)
      .then((res) => {
        setSearchRes(res.trim())
      })
      .catch((error) => console.log(error));
    } 
  }

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchPage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                onChange={searchHandler}
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
              {
                searchRes !== [] && searchRes.map((book) => <Book key={book.id} book={book} updateScreen={updateScreen} />)
              }
            </ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              {shelves.map((shelf, i) => (
                <Shelf
                  key={i}
                  books={Object.values(shelf)[0]}
                  shelfTitle={Object.keys(shelf)[0]}
                  updateScreen={updateScreen}
                />
              ))}
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchPage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
