import { useState, useEffect } from "react";
import * as BooksAPI from "../BooksAPI.js";
import { Link } from "react-router-dom";
import Book from "./Book";

const SearchPage = ({ updateScreen }) => {
  const [searchRes, setSearchRes] = useState([]);
  const [searchWord, setSearchWord] = useState([]);
  const [isVisible, setIsVisible] = useState(true);
  const [myBooks, setMyBooks] = useState([]);


  function searchHandler(event) {
    setSearchWord(event.target.value.trim());
    if (searchWord !== "") {
      BooksAPI.search(searchWord)
        .then((res) => {
          const arr = [];
          Array.isArray(res) ? (arr = res) : (arr = []);
          const booksInResult = myBooks.filter()
        })
        .catch((error) => console.log(error));
      console.log(searchRes);
    } else {
      setSearchRes([]);
    }
    setIsVisible(false);
  }

  function updateSearch() {
    BooksAPI.getAll()
      .then((allBooks) => {
        setMyBooks(allBooks);
      })
      .catch((error) => console.log(error));
  }

  useEffect(() => {
    updateSearch();
  }, []);

  return (
    <div className="app">
      <div className="search-books">
        <div className="search-books-bar">
          <Link to="/" className="close-search">
            Close
          </Link>
          <div className="search-books-input-wrapper">
            <input
              onChange={searchHandler}
              type="text"
              placeholder="Search by title, author, or ISBN"
            />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            {searchRes.map((book) => (
              <Book key={book.id} book={book} updateScreen={updateSearch} />
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
};

export default SearchPage;
